import React from 'react';
import getGenres from '../services/fakeGenreService';

const categories = props =>
{
    const {onGenreChange} = props;
    const allGenres = getGenres();

    return ( 
        <div className="list-group">
            <li 
                key="allGenres" 
                href="/" 
                className={CheckClass('All')}
                onClick={() => onGenreChange('All')}>
                > All</li>

            {allGenres.map(genre => (
                <li 
                key={genre._id} 
                className={CheckClass(genre.name)}
                onClick={() => onGenreChange(genre.name)}>
                {genre.name}</li>
            ))}
        </div>
    );

}
function CheckClass(genre, selectedGenre)
{
    const classButtons = "list-group-item";
    return genre === selectedGenre? classButtons + ' active' :  classButtons;
}

export default categories;
