import { LOGIN_SUCCESS, LOGIN_ERROR, SIGNOUT, SIGNUP_ERROR, SIGNUP_SUCCESS } from '../actions/actionTypes';

const initState = {
    loggedIn: false,
    userData: {},
    authMessage: null
};

const authReducer = (state = initState, action) => {
    switch (action.type) {
        case LOGIN_SUCCESS:
            return {
                ...state,
                loggedIn: true,
                userData: action.payload.data.userData,
                authMessage: action.payload.data.message
            }
        case LOGIN_ERROR:
            return {
                ...state,
                userData: {},
                loggedIn: false,
                authMessage: action.error.response.data.error
            }
        case SIGNUP_SUCCESS:
            return {
                ...state,
                loggedIn: true,
                userData: action.payload.data.userData,
                authMessage: action.payload.data.message
            }
        case SIGNUP_ERROR:
            return {
                ...state,
                loggedIn: false,
                userData: action.payload.data.userData,
                authMessage: action.payload.data.message
            }

        case SIGNOUT:
            return {
                ...state,
                userData: {},
                loggedIn: false,
                authMessage: null
            }
        default:
            return state;
    }
}

export default authReducer;
