export default function categorize(allMovies, genre)
{
    if(genre === "All")
    {
        return allMovies;
    }
    else {
        const movies = allMovies.filter(movie => movie.genre.name === genre)
        // allMovies.map(movie =>
        // {
        //     if(movie.genre.name === genre)
        //     { 
        //         movies.push(movie);
        //     }
        // })c
        return movies;
    }
}
