import _ from 'lodash';
import { getMovies } from "../services/fakeMovieService";

export default function categorize(category)
{
    const allMovies = getMovies()
    if(category === "All")
    {
        return allMovies;
    }
    else {
        let movies =[];
        allMovies.map(movie =>
        {
            if(movie.genre.name === category)
            { 
                movies.push(movie);
            }
        })
        return movies;
    }
}
