import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import Pagination from './common/Pagination.jsx';
import paginate from '../Utils/paginate';
import MoviesTable from './moviesTable.jsx';

import Categories from "./categories.jsx";
import categorize from '../Utils/categorize';
import getGenres from "../services/fakeGenreService.js";


class Movies extends Component {
  state = {
    allMovies:[],
    genres:[],
    pageSize: 4,
    currentPage: 1,
    currentGenre: "All",
    selectecGenre:''
  };

  componentDidMount()
  {
    this.setState({ allMovies: getMovies(), genres: getGenres()})
  }

  handleDelete = movie => {
    const movies = this.state.movies.filter(m => m._id !== movie._id);
    this.setState({ movies });
  };

  handlePageChange = (page) =>
  {
      this.setState({
        currentPage: page
      });
  }
  handleLike = movie => {  
    const movies = [...this.state.allMovies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    console.log(movie);
    this.setState({ movies });
  };

  handleSort = path =>{
    console.log(path);
  }

  handleGenreChange = genre =>
  {
    this.setState({
      currentGenre: genre
    });
  }

  render() {

    const {
      allMovies, 
      currentGenre, 
      currentPage, 
      pageSize, 
      genres, 
    } = this.state;



    const categorizedMovie = categorize(allMovies, currentGenre) 
    const movies = paginate(categorizedMovie, currentPage, pageSize);

    const { length: count } = categorizedMovie;
    if (count === 0) return <p>There are no movies in the database.</p>;

    return (
      <React.Fragment>

      <div className="row">

      <div className='col-lg-1' style={{top:'50px',margin:'auto'}}>
        <Categories
          currentGenre = {currentGenre}
          onGenreChange = {this.handleGenreChange}
          allGenres={genres}
        />
      </div>

      <div className="col-lg-2" style={{width:'80%', top:'50px'}}>

        <p>You have {count} movies available.</p>

        <MoviesTable
          onDelete={this.handleDelete}
          onLike={this.handleLike}
          movies={movies}
          onSort={this.handeSort}
        />

      </div>


    </div>
    <Pagination
        itemsCount={categorizedMovie.length}
        pageSize={this.state.pageSize}
        onPageChange={this.handlePageChange}
        currentPage={this.state.currentPage}
      />
      </React.Fragment>
    );
  }
}

export default Movies;
