import React from 'react';
import Like from "./common/like.jsx";

const MoviesTable = (props) => {

    const { movies, onDelete, onLike, onSort} = props;

    return ( 
        <table className="table">
            <thead>
              <tr>
                <th style={{textAlign:'center'}}>Title</th>
                <th style={{textAlign:'center'}}>Genre</th>
                <th style={{textAlign:'center'}}>Stock</th>
                <th style={{textAlign:'center'}}>Rate</th>
                <th style={{textAlign:'center'}}>Favourite</th>
                <th style={{textAlign:'center'}}>Remove</th>
              </tr>
            </thead>

            <tbody>
              {movies.map(movie => (
                <tr key={movie._id}>
                  <td onClick={() => onSort()}>{movie.title}</td>
                  <td onClick={() => onSort()}>{movie.genre.name}</td>
                  <td onClick={() => onSort()}>{movie.numberInStock}</td>
                  <td onClick={() => onSort()}>{movie.dailyRentalRate}</td>

                  <td>
                    <Like
                      liked={movie.liked}
                      onLike={() => onLike(movie)}
                    />
                  </td>

                  <td>
                    <button
                      onClick={() => onDelete(movie)}
                      className="btn btn-danger btn-sm"
                    >
                      Delete
                    </button>
                  </td>

                </tr> 
              ))}
            </tbody>

          </table>
     );
}

export default MoviesTable;
