import React from 'react'
import Joi from '@hapi/joi';
import Form from './common/form';
import Axios from 'axios';
import _ from 'lodash';
class LoginForm extends Form {
    constructor() {
        super();
    }
    state = {
        data:{
            email:"",
            password:""
        },
        errors: { },
    }

    schema = {
        email: Joi.string().email().required().label("Email"),    // .label() is for what to show to the user, instead of "username", it will show "Username"
        password: Joi.string().required().regex(/^[a-zA-Z0-9]{8,30}$/).label("Password"),    // Just a markup
    }

    handleSubmit = (e) => {
        super.handleSubmit(e);

        if(_.isEmpty(this.state.errors))
        {
            console.log("requesting")
            Axios
            .post('/api/users/login', this.state.data)
            .then(result => console.log(result))
            .catch(error => console.log(error.status))
        }
    }

    render() { 
        return ( 
            <div className="container">
               <h1>Login</h1>
                <form onSubmit={this.handleSubmit}>
                    {this.renderInput("email", "Email", "email", "Please enter your email...", true)}
                    {this.renderInput("password", "Password", "password", "Please enter your password...")}
                    {this.renderSubmitButton("Login")}
                </form>
            </div>
        );
    }
}

export default LoginForm;
