import React from 'react';
import Form from './common/form';
import Joi from '@hapi/joi';
import Axios from 'axios';

class RegisterForm extends Form {
    constructor() {
        super();
    }
    state = { 
        data:{
            email:"",
            password:"",
            passwordRepeat:""
        },
        errors:{},
        authError: ""
    }

    handleSubmit(e){
        super.handleSubmit(e);

        const { password, passwordRepeat, email } = this.state.data;
        if(password !== passwordRepeat) this.setState({ authError: "The passwords doesn't match." });
        else {
            this.setState({ authError: "" });

            Axios
            .post('/api/users/signup', { email, password })
            .then(result => console.log(result))
            .catch(error => this.setState({ authError: error.response.data.error }))
        }
    } 

    schema = {
        email: Joi.string().email().required().label("Email"),
        password: Joi.string().min(8).regex(/^[a-zA-Z0-9]{8,30}$/).required().label("Password"),
        passwordRepeat: Joi.string().min(8).required().label("username"),
    }
    render() { 
        const { authError } = this.state;
        return ( 
            <div className="container">
                <h1>Register Form</h1>
                <form onSubmit={this.handleSubmit}>
                    {this.renderInput("email", "Email", "email", "Please enter your email...", true)}
                    {this.renderInput("password","Passowrd", "password", "Enter a new password...")}
                    {this.renderInput("passwordRepeat","Repeat Password","password", "Repeat your password...")}
                    {authError && <p className="bg-danger text-white ">{authError}</p>}
                    {this.renderSubmitButton("Sign Up")}
                </form>

            </div>
        );
    }
}

export default RegisterForm;
