import React, {Component} from 'react';
import Like from "./common/like.jsx";
import TableMaker from '../components/common/TableMaker.jsx';

class MoviesTable extends Component {

    render() { 

        const { movies, onDelete, onLike} = this.props;
        return ( 
            <table className="table">
                <thead>
                    <tr>
                    <th onClick={() => this.raiseSort('title')} style={{textAlign:'center'}}>Title</th>
                    <th onClick={() => this.raiseSort('genre.name')} style={{textAlign:'center'}}>Genre</th>
                    <th onClick={() => this.raiseSort('numberInStock')}style={{textAlign:'center'}}>Stock</th>
                    <th >Rate</th>
                    <th style={{textAlign:'center'}}>Favourite</th>
                    <th style={{textAlign:'center'}}>Remove</th>
                    </tr>
                </thead>

                <tbody>
                    {movies.map(movie => (
                    <tr key={movie._id}>
                        <td>{movie.title}</td>
                        <td>{movie.genre.name}</td>
                        <td>{movie.numberInStock}</td>
                        <td>{movie.dailyRentalRate}</td>

                        <td>
                        <Like
                            liked={movie.liked}
                            onLike={() => onLike(movie)}
                        />
                        </td>

                        <td>
                        <button
                            onClick={() => onDelete(movie)}
                            className="btn btn-danger btn-sm"
                        >
                            Delete
                        </button>
                        </td>

                    </tr> 
                    ))}
                </tbody>

                </table>
            );
    }
}

export default MoviesTable;
