import _ from 'lodash';

export default function categorize(allMovies, category)
{
    if(category === "All")
    {
        console.log("all")
        return allMovies;
    }
    else {
        console.log("not all")
        const movies = allMovies.map(movie =>
        {
            if(movie.genre.name === category)
            { 
                return movie;
            }
        })
        console.log(movies.count);
        return movies;
    }
}
