import _ from 'lodash';

export default function categorize(allMovies, category)
{
    console.log(allMovies);
    const movies = allMovies.map(allMovies, movie =>
    {
        console.log(movie);
        if(movie.genre.name === category) return movie;
    })

    return _(movies);
}
