import React from 'react';

const categories = props =>
{
    const {onGenreChange, category, allGenres} = props;

    return ( 
        <div className="list-group">
            <li 
                key="allGenres" 
                href="/" 
                className={CheckClass('All', category)}
                onClick={() => onGenreChange('All')}
                > All</li>

            {allGenres.map(genre => (
                <li 
                    key={genre._id} 
                    className={CheckClass(genre.name ,category)}
                    onClick={() => onGenreChange(genre.name)}>
                    {genre.name}
                </li>
            ))}
        </div>
    );

}
function CheckClass(genre, currentGenre)
{
    const classButtons = "list-group-item";
    return genre === currentGenre? classButtons + ' active' :  classButtons;
}

export default categories;
