export default function categorize(allMovies ,category)
{

    if(category === "All")
    {
        return allMovies;
    }
    else {
        let movies =[];
        allMovies.map(movie =>
        {
            if(movie.genre.name === category)
            { 
                movies.push(movie);
            }
        })
        return movies;
    }
}
