import React from 'react'
import Joi from '@hapi/joi';
import Form from './common/form';
import Axios from 'axios';
import _ from 'lodash';
import Input from './common/input';
import { connect } from 'react-redux';
import { SignIn } from '../actions/authAction';

class LoginForm extends Form {
    state = {
        data:{
            email:"",
            password:""
        },
        errors: { },
        authError: ""
    }

    schema = {
        email: Joi.string().email().required().label("Email"),    
        password: Joi.string().required().label("Password"), 
    }

    handleSubmit = (e) => {
        super.handleSubmit(e);

        if(_.isEmpty(this.state.errors)) {
            console.log("requesting")
            Axios
            .post('/api/users/login', this.state.data)
            .then(result => { this.setState({ authError: "" }); console.log(result)})
            .catch(error => this.setState({ authError: error.response.data.error }))
        }
    }

    render() { 
        const { authError, errors } = this.state;
        const { email, password } = this.state.data;

        return ( 
            <div className="background-container">
                <div className="container">
                <h1 className="main-header">Login</h1>
                    <form onSubmit={this.handleSubmit}>
                        <Input 
                            name="email" 
                            label="Email" 
                            error={errors["email"]}
                            iconClass="fas fa-envelope"
                            onChange={this.handleChange}
                            placeholder="Please enter your email..."
                            value={email}
                            autoFocus
                        />
                        <Input 
                            name="password" 
                            label="Password" 
                            error={errors["password"]}
                            iconClass="fas fa-key"
                            onChange={this.handleChange}
                            placeholder="Please enter your password..."
                            value={password}
                        />

                        {authError && <p className="bg-danger text-white ">{authError}</p>}
                        {this.renderSubmitButton("Login")}
                    </form>
                </div>
            </div>
        );
    }
}
const mapStateToProps = state => {
    console.log(state)
    return { 
        authError: state.auth.authError
    }
}
const mapDispatchToProps = dispatch => {
    console.log(dispatch);
    return {
        SignIn: (creds) => dispatch(SignIn(creds))  
    }
}

export default connect(mapStateToProps, mapDispatchToProps) (LoginForm);
