import React from 'react'
import Joi from 'joi-browser';
import Form from './common/form';

class LoginForm extends Form {

    state = {
        data:{
            username:"",
            password:""
        },
        errors: { },
    }

    schema = {
        username: Joi.string().required().label("Username"),    // .label() is for what to show to the user, instead of "username", it will show "Username"
        password: Joi.string().required().label("Password"),    // Just a markup
    }

    doSubmit = () =>
    {
        //Call the server
        console.log('Logged in.')
    }
    render() { 
        return ( 
            <div className="container">

                <form onSubmit={this.handleSubmit}>
                <h1>Login</h1>
                    {this.renderInput("username", "Username", "text", "Please enter your username...")}
                    {this.renderInput("password", "Password", "password", "Please enter your password...")}
                    {this.renderButton("Login")}
                </form>
            </div>
        );
    }
}

export default LoginForm;
