import React from 'react';
import Form from './common/form';
import Joi from 'joi-browser';
import getGenres from '../services/fakeGenreService';

class AddMovieForm extends Form {

    state = {
        data:{
            title:"",
            genreID:"",
            numberInStocks:"",
            rate:"",
        },
        genres:[],
        errors:{}
    }

    schema = {
        title: Joi.string().required().label("Title"),
        genre: Joi.string().required().label("Genre"),
        numberInStock: Joi.number().min(0).max(999).required().label("Number In Stocks"),
        rate: Joi.number().min(0).max(10).required().label("Rate")
    }
    doSumit = () =>
    {
        console.log("added the movie")
    }
    componentDidMount()
    {
        const genres = getGenres();
        this.setState(genres);
    }

    render()
    {
        console.log(this.state.genres)
        return ( 
            <div>
               <form>
                   {this.renderInput("title", "Title")}
                   {this.renderSelect("genreID", "Genre", this.state.genres)}
                   {this.renderInput("numberInStock", "NumberInStock", "number")}
                   {this.renderInput("rate", "Rate", "number")}

                   {this.renderButton("Add Movie")}
               </form>
            </div>
        );
    }

}
export default AddMovieForm;
