import React from 'react'

export const SearchBar = ( {onSearch, name} ) => {
    return (
        <div className="active-cyan-3 active-cyan-4 mb-4">
            <input 
                className="form-control" 
                name={name}
                onChange={onSearch}
                type="text" 
                placeholder="Search" 
                aria-label="Search"
            />
        </div>
    )
}

export function SearchItem (item, Items, filterBy) 
{

    const filteredItems = Items.forEach(element => {
        console.log(element[filterBy])
    });
    console.log(filteredItems);
   return filteredItems;
}
