import React, { Component } from "react";
import Like from "./common/like.jsx";
import { getMovies } from "../services/fakeMovieService";
import Pagination from './common/Pagination.jsx';
import paginate from '../Utils/paginate';

import Categories from "./categories.jsx";
import categorize from '../Utils/categorize';
import getGenres from "../services/fakeGenreService.js";

import { Container, Row, Col } from 'reactstrap';


class Movies extends Component {
  state = {
    allMovies: getMovies(),
    pageSize: 4,
    currentPage: 1,
    currentGenre: "All",
    gentes: getGenres()
  };

  handleDelete = movie => {
    const movies = this.state.movies.filter(m => m._id !== movie._id);
    this.setState({ movies });
  };

  handlePageChange = (page) =>
  {
      this.setState({
        currentPage: page
      });
  }

  handleGenreChange = genre =>
  {
    this.setState({
      currentGenre: genre,
    });
  }

  handleLike = movie => {  
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };
  componentDidUpdate()
  {
    console.log(this.state.currentGenre);
  }

  render() {
    const {currentPage, pageSize, allMovies, currentGenre} = this.state;
    const { length: count } = allMovies;
    const categorizedMovie = categorize(allMovies,this.state.currentGenre);

    if (count === 0) return <p>There are no movies in the database.</p>;

    const movies = paginate(categorizedMovie, currentPage, pageSize);

    return (
      <Row>
      <Col>
      <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th>Favourite</th>
              <th>Remove</th>
            </tr>
          </thead>
          <tbody>
            {movies.map(movie => (
              <tr key={movie._id}>
                <td>{movie.title}</td>
                <td>{movie.genre.name}</td>
                <td>{movie.numberInStock}</td>
                <td>{movie.dailyRentalRate}</td>
                <td>
                  <Like
                    liked={movie.liked}
                    onClick={() => this.handleLike(movie)}
                  />
                </td>
                <td>
                  <button
                    onClick={() => this.handleDelete(movie)}
                    className="btn btn-danger btn-sm"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </Col>

      <Col>
        <Categories
          category = {currentGenre}
          onGenreChange = {this.handleGenreChange}
        />
      </Col>
    </Row>

    );
  }
}

export default Movies;
