import React from 'react';
import Form from './common/form';
import Joi from '@hapi/joi';
import Axios from 'axios';
import Input from './common/input';

class RegisterForm extends Form {
    constructor() {
        super();
    }
    state = { 
        data:{
            email:"",
            password:"",
            passwordRepeat:""
        },
        errors:{},
        authError: ""
    }

    handleSubmit = (e) => {
        super.handleSubmit(e);

        const { password, passwordRepeat, email } = this.state.data;
        if(password !== passwordRepeat) this.setState({ authError: "The passwords doesn't match." });
        else {
            Axios
            .post('/api/users/signup', { email, password })
            .then(result => { 
                this.setState({ authError: "" }); 
                console.log(result)}
            )
            .catch(error => this.setState({ authError: error.response.data.error }))
        }
    } 

    schema = {
        email: Joi.string().email().required().label("Email"),
        password: Joi.string().min(8).required().label("Password"),
        passwordRepeat: Joi.string().required().label("username"),
    }
    render() { 
        const { authError, errors } = this.state;
        const { email, password, passwordRepeat } = this.state.data;

        return ( 
            <div className="background-container">
                <div className="container">
                    <h1 className="main-header">Register Form</h1>
                    <form onSubmit={this.handleSubmit}>
                        <Input 
                            name="email" 
                            label="Email" 
                            error={errors["email"]}
                            iconClass="fas fa-envelope"
                            onChange={this.handleChange}
                            placeholder="Please enter your email..."
                            value={email}
                            autoFocus
                        />
                        <Input 
                            name="password" 
                            label="Password" 
                            error={errors["password"]}
                            iconClass="fas fa-key"
                            onChange={this.handleChange}
                            placeholder="Please enter your password..."
                            value={password}
                        /> 
                          <Input 
                            name="passwordRepeat" 
                            label="Repeat Password" 
                            error={errors["passwordRepeat"]}
                            iconClass="fas fa-key"
                            onChange={this.handleChange}
                            placeholder="Repeat your password..."
                            value={passwordRepeat}
                        /> 

                        {authError && <p className="bg-danger text-white">{authError}</p>}

                        {this.renderSubmitButton("Sign Up")}
                        <button type="submit" className="btn special-btn" disabled={this.validate()}>
                            Sign Up
                        </button>
                    </form>
                </div>
            </div>
        );
    }
}

export default RegisterForm;
