import React from 'react';
import getGenres from '../services/fakeGenreService';

const categories = props =>
{
    const {onGenreChange} = props;
    const allGenres = getGenres();
    const classButtons = "list-group-item list-group-item-action";

    return ( 
        <React.Fragment>
           <div className="list-group">

           <a key="allGenres" href="/" className={classButtons}> All</a>
            {allGenres.map(genre => (

                <a 
                key={genre._id} 
                className={props.category === genre? classButtons + ' active' :  classButtons}
                onClick={() => onGenreChange(genre.name)}>
                {genre.name}</a>
            ))}

            </div>
        </React.Fragment>
    );
}

export default categories;
