import React from 'react';
import getGenres from '../services/fakeGenreService';

const categories = props =>
{
    const {onGenreChange,category} = props;
    const allGenres = getGenres();
    const classButtons = "list-group-item";

    return ( 
        <div className="list-group">
            <li 
                key="allGenres" 
                href="/" 
                className={category === 'All' ? classButtons + ' active' :  classButtons}
                > All</li>

            {allGenres.map(genre => (
                <li 
                key={genre._id} 
                className={category === genre.name? classButtons + ' active' :  classButtons}
                onClick={() => onGenreChange(genre.name)}>
                {genre.name}</li>
            ))}
        </div>
    );
}

export default categories;
