import React, { Component } from 'react'
import Input from './common/input';
import Joi from 'joi-browser';

class LoginForm extends Component {
    state = {
        data:{
            username:"",
            password:""
        },
        errors: { }
    }

    schema = {
        username: Joi.string().required().label("Username"),    // .label() is for what to show to the user, instead of "username", it will show "Username"
        password: Joi.string().required().label("Password"),    // Just a markup
    }

    handleSubmit = (e) =>
    {
        e.preventDefault();
        const errors = this.validate();
        this.setState({errors: errors || {} }); //If error occur set it in state or if its not set empty object
        if(errors) return;

        //Call the server
        console.log('submited')
    }
    validate = e => {
        const options = { abortEarly: false }
        const result = Joi.validate(this.state.data, this.schema, options);
        if(!result.error) return null;

        const errors = {};
        result.error.details.forEach(element => {
            errors[element.path[0]] = element.message;
        });
        return errors;
    }

    handleChange = ({currentTarget: input}) =>
    {
        const errors = {...this.state.errors}
        const errorMessage = this.validateProperty(input);

        if(errorMessage) errors[input.name] = errorMessage;
        else delete errors[input.name];


        const data = {...this.state.data} 
        data[input.name] = input.value;
        this.setState({ data, errors });
    }

    validateProperty = input =>
    {
        const { name, value } = input;

        const obj = { [name]: value}
        const schema = { [name]: this.schema[name] }

        const { error } = Joi.validate(obj, schema);

        return error ? error.details[0].message : null
    }

    render() { 
        const { data, errors } = this.state;
        return ( 
            <div>
                <h1>Login</h1>
                <form onSubmit={this.handleSubmit}>

                <Input
                    name="username"
                    label="Username"
                    error={errors.username}
                    value={data.username}
                    onChange={this.handleChange}
                />
                <Input
                    label="Password"
                    name="password"
                    error={errors.password}
                    value={data.password}
                    onChange={this.handleChange}
                />


                <button 
                    className="btn btn-primary"
                    disabled={this.validate()}>
                    Login
                </button>

                </form>
            </div>
        );
    }
}

export default LoginForm;
