import React from 'react';

const input = (props) => {
    const { name, account, label, value, onChange} = props;

    return ( 
        <div className="form-group">
        <label htmlFor={name}> {label}
            <input 
                value={value} 
                onChange={onChange}
                autoFocus 
                name={name}
                id={name}
                type='text' 
                className="form-control"
            />
        </label>
    </div>
     );
}

export default input;
