export default function categorize(allMovies, genre)
{
    console.log('here');
    if(genre === "All")
    {
        return allMovies;
    }
    else {
        let movies =[];
        allMovies.map(movie =>
        {
            if(movie.genre.name === genre)
            { 
                movies.push(movie);
            }
        })
        return movies;
    }
}
