import React from 'react'
import sampleIcon from '../../images/noposter.jpg';
import '../../css/movieCard.css';
import fs from 'fs';


const MovieCard = ({ movie }) => {
    console.log(movie.image)
    const { title, rating, price, genre, image } = movie;
    let encodedImage = new Buffer(image.data, 'binary').toString('base64');
    var vals = 'data:image/jpeg;base64,' + encodedImage
    return ( 
        <div className="card-box col-lg-3 col-md-4">
            <img src={vals ? vals : sampleIcon} alt="a"/>
            <span>1.3</span>
            <p>{title}</p>
            <small>{genre}</small>
        </div>
    );
}

export default MovieCard;
