import React, { Component } from "react";
import Like from "./common/like.jsx";
import { getMovies } from "../services/fakeMovieService";
import Pagination from './common/Pagination.jsx';
import paginate from '../Utils/paginate';

import Categories from "./categories.jsx";
import categorize from '../Utils/categorize';
import getGenres from "../services/fakeGenreService.js";


class Movies extends Component {
  state = {
    allMovies: getMovies(),
    pageSize: 4,
    currentPage: 1,
    currentGenre: "All",
    gentes: getGenres()
  };

  handleDelete = movie => {
    const movies = this.state.movies.filter(m => m._id !== movie._id);
    this.setState({ movies });
  };

  handlePageChange = (page) =>
  {
      this.setState({
        currentPage: page
      });
  }
  handleLike = movie => {  
    const movies = [...this.state.allMovies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    console.log(movie);
    this.setState({ movies });
  };


  handleGenreChange = genre =>
  {
    const categorizedMovie = categorize(this.state.allMovies ,genre);
    this.setState({
      currentGenre: genre,
      allMovies:categorizedMovie
    });
  }

  render() {

    const {allMovies, currentGenre, currentPage, pageSize} = this.state;
    const { length: count } = allMovies;

    const movies = paginate(allMovies, currentPage, pageSize);

    if (count === 0) return <p>There are no movies in the database.</p>;

    return (
      <React.Fragment>
      <div className="row">

      <div className='col-lg-1' style={{top:'50px',margin:'auto'}}>
        <Categories
          category = {currentGenre}
          onGenreChange = {this.handleGenreChange}
        />
      </div>

      <div className="col-lg-2" style={{width:'80%', top:'50px'}}>
        <table className="table">

            <thead>
              <tr>
                <th style={{textAlign:'center'}}>Title</th>
                <th style={{textAlign:'center'}}>Genre</th>
                <th style={{textAlign:'center'}}>Stock</th>
                <th style={{textAlign:'center'}}>Rate</th>
                <th style={{textAlign:'center'}}>Favourite</th>
                <th style={{textAlign:'center'}}>Remove</th>
              </tr>
            </thead>

            <tbody>
              {movies.map(movie => (
                <tr key={movie._id}>
                  <td>{movie.title}</td>
                  <td>{movie.genre.name}</td>
                  <td>{movie.numberInStock}</td>
                  <td>{movie.dailyRentalRate}</td>

                  <td>
                    <Like
                      liked={movie.liked}
                      onLike={() => this.handleLike(movie)}
                    />
                  </td>

                  <td>
                    <button
                      onClick={() => this.handleDelete(movie)}
                      className="btn btn-danger btn-sm"
                    >
                      Delete
                    </button>
                  </td>

                </tr> 
              ))}
            </tbody>

          </table>

      </div>


    </div>
    <Pagination
        itemsCount={count}
        pageSize={this.state.pageSize}
        onPageChange={this.handlePageChange}
        currentPage={this.state.currentPage}
      />
      </React.Fragment>
    );
  }
}

export default Movies;
