import React from 'react'
import noPosterIMG from '../../images/noposter.jpg';
import '../../css/movieCard.css';

const MovieCard = ({ movie }) => {
    const { title, rate, price, genre, image, description } = movie;
    let encodedImage = new Buffer(image.data, 'binary').toString('base64');
    var coverImage = 'data:image/jpeg;base64,' + encodedImage
    return ( 
        <div onClick={() => ShowCard(title)} id={title} className="card-container col-lg-3 col-md-4">
             <div className="thecard">
                <div className="front-card">
                    <img src={coverImage ? coverImage : noPosterIMG} alt="coverImage"/>
                    <span>{rate}</span>
                </div>

                <div className="back-card">
                    <p> {description} </p>
                </div>
            </div>      

            <div className="details">
                <p>{title}</p>
                <small>{genre}</small>
            </div> 
        </div>
    );
}

const ShowCard = (cardTitle) => {
    const card = document.querySelector(`#${cardTitle}`);
    card.classList.toggle('show-card');
}

export default MovieCard;
