import React from 'react'
import StringSimilarity from 'string-similarity'; 


export const SearchBar = ( {onSearch, name} ) => {
    return (
        <div className="active-cyan-3 active-cyan-4 mb-4">
            <input 
                className="form-control" 
                name={name}
                onChange={onSearch}
                type="text" 
                placeholder="Search" 
                aria-label="Search"
            />
        </div>
    )
}

export function SearchItem (item, Items, filterBy) 
{
    // const filterString = Items.get(element => element[filterBy] )
    let filteredString = [];
    Items.forEach(element => {
        filteredString.push(element[filterBy])
    });

    const filteredItems = StringSimilarity.findBestMatch(item, filteredString)
    console.log(filteredItems);


    return Items;
}
